import { Component } from '@angular/core';

@Component({
  selector: 'app-gift3',
  templateUrl: './gift3.component.html',
  styleUrls: ['./gift3.component.css']
})
export class Gift3Component {
  correct = "Ao infinito e além?"

  a1_click(){
    const a1 = document.getElementById("alternative1")

    a1?.classList.add("error")
    setTimeout(function(){
      a1?.classList.remove("error")
    }, 900)
  }

  a2_click(){
    const a2 = document.getElementById("alternative2")
    const ans = document.getElementById("ans")

    a2?.classList.add("text-success")
    this.correct = "ACERTOU"
    ans?.classList.add("hit")
  }

  a3_click(){
    const a3 = document.getElementById("alternative3")

    a3?.classList.add("error")
    setTimeout(function(){
      a3?.classList.remove("error")
    }, 900)
  }

  a4_click(){
    const a4 = document.getElementById("alternative4")

    a4?.classList.add("error")
    setTimeout(function(){
      a4?.classList.remove("error")
    }, 900)
  }
}
<main class="container-fluid px-0 px-md-5 bg-roxo d-flex justify-content-center align-items-center">
  <div class="container bg-c0 p-5 rounded-5">

    <section class="row">
      <div class="col-2">
        <img src="./../../../../assets/svg/back-gift.svg" routerLink="/gift/gift2">
      </div>
      <h1 class="col-8 text-center text-rosa">Se acertar terá que me provar ;)</h1>
    </section>

    <section class="bg-c2 p-4 mt-4 rounded-5 d-flex justify-content-center align-items-center">
      <div class="w-90 py-3 d-flex flex-column justify-content-center">

        <h1 class="bg-c1 m-0 p-1 rounded-4 text-center text-azul-escuro">Até onde vai meu amor por você?</h1>

        <div class="row mt-4">
          <div class="col-6 d-flex align-items-center">
            <div class="indicator"></div>
            <p class="h-100 m-0 ms-3 d-flex align-items-center" id="alternative1" (click)="a1_click()">Jairo lanches?</p>
          </div>
          <div class="col-6 d-flex align-items-center">
            <div class="indicator"></div>
            <p class="h-100 m-0 ms-3 d-flex align-items-center" id="alternative2" (click)="a2_click()">{{correct}}</p>
          </div>
        </div>

        <div class="row my-4">
          <div class="col-6 d-flex align-items-center">
            <div class="indicator"></div>
            <p class="h-100 m-0 ms-3 d-flex align-items-center" id="alternative3" (click)="a3_click()">Na Lenha Pizzaria?</p>
          </div>
          <div class="col-6 d-flex align-items-center">
            <div class="indicator"></div>
            <p class="h-100 m-0 ms-3 d-flex align-items-center" id="alternative4" (click)="a4_click()">GOAT?</p>
          </div>
        </div>

        <h1 class="bg-c1 m-0 p-1 rounded-4 text-center text-roxo container-tip" id="ans">
          <p class="front"></p>
          <p class="back">Esse vai receber pessoalmente S2</p>
        </h1>

      </div>
    </section>

  </div>
</main>
