import { Component } from '@angular/core';

@Component({
  selector: 'app-gift2',
  templateUrl: './gift2.component.html',
  styleUrls: ['./gift2.component.css']
})
export class Gift2Component {
  correct = dias() + "?"

  a1_click(){
    const a1 = document.getElementById("alternative1")

    a1?.classList.add("error")
    setTimeout(function(){
      a1?.classList.remove("error")
    }, 900)
  }

  a2_click(){
    const a2 = document.getElementById("alternative2")

    a2?.classList.add("error")
    setTimeout(function(){
      a2?.classList.remove("error")
    }, 900)
  }

  a3_click(){
    const a3 = document.getElementById("alternative3")

    a3?.classList.add("error")
    setTimeout(function(){
      a3?.classList.remove("error")
    }, 900)
  }

  a4_click(){
    const a4 = document.getElementById("alternative4")
    const ans = document.getElementById("ans")

    a4?.classList.add("text-success")
    this.correct = "ACERTOU"
    ans?.classList.add("hit")
  }
}

const dateStart = new Date("2019, 03, 22")

function dias(){

  let dataAtual = new Date()
  let dataPassada = new Date(dateStart)
  let dataMilisegundos = Math.abs(dataAtual.getTime() - dataPassada.getTime())
  let diferenca = Math.ceil(dataMilisegundos / (1000*60*60*24))

  return diferenca
}
<main class="container-fluid px-0 px-md-5 bg-rosa d-flex justify-content-center align-items-center">
  <div class="container bg-c0 p-5 rounded-5">

    <section class="row">
      <div class="col-2">
        <img src="./../../../../assets/svg/back-gift.svg" routerLink="/gift/gift1">
      </div>
      <h1 class="col-8 text-center text-rosa">Acerte, ganhe uma dica e procure.</h1>
      <div class="col-2 d-flex justify-content-end">
        <img src="./../../../../assets/svg/next-gift.svg" routerLink="/gift/gift3">
      </div>
    </section>

    <section class="bg-c2 p-4 mt-4 rounded-5 d-flex justify-content-center align-items-center">
      <div class="w-90 py-3 d-flex flex-column justify-content-center">

        <h1 class="bg-c1 m-0 p-1 rounded-4 text-center text-azul-escuro">Há quantos dias estamos juntos?</h1>

        <div class="row mt-4">
          <div class="col-6 d-flex align-items-center">
            <div class="indicator"></div>
            <p class="h-100 m-0 ms-3 d-flex align-items-center" id="alternative1" (click)="a1_click()">1426?</p>
          </div>
          <div class="col-6 d-flex align-items-center">
            <div class="indicator"></div>
            <p class="h-100 m-0 ms-3 d-flex align-items-center" id="alternative2" (click)="a2_click()">1456?</p>
          </div>
        </div>

        <div class="row my-4">
          <div class="col-6 d-flex align-items-center">
            <div class="indicator"></div>
            <p class="h-100 m-0 ms-3 d-flex align-items-center" id="alternative3" (click)="a3_click()">1452?</p>
          </div>
          <div class="col-6 d-flex align-items-center">
            <div class="indicator"></div>
            <p class="h-100 m-0 ms-3 d-flex align-items-center" id="alternative4" (click)="a4_click()">{{correct}}</p>
          </div>
        </div>

        <h1 class="bg-c1 m-0 p-1 rounded-4 text-center text-roxo container-tip" id="ans">
          <p class="front"></p>
          <p class="back">Dica: Thutis, thatis, thutis, thatis ...</p>
        </h1>

      </div>
    </section>

  </div>
</main>
